const initialState = {
    students : [],
}

const studentReducer = (state = initialState, action) => {
    switch (action.type) {
        case "CREATE_STUDENT": {
            const students = [...state.students, action.student];
            return { ...state, students };
          }
          case "DELETE_STUDENT": {
            const students = state.students.filter((student) => students.id !== action.studentId);
            return { ...state, students };
          }
          case "SELECT_STUDENT": {
            return { ...state, selectedStudent: action.student };
          }
          case "UPDATE_USER": {
            const users = state.users.map((user) => {
              if (user.id === action.userId) {
                return { ...action.user, id: action.userId };
              }
              return user;
            });
            return { ...state, users, selectedUser: {} };
          }
        default:
            return state;
    }
}
export  default studentReducer;
