import React, { Component } from "react";

class StudentForm extends Component {
    constructor(props) {
      super(props)
    
      this.state = {
        values : {
            id: "",
            name: "",
            phone: "",
            email: "",
        }
      }
    }
    handleChange = (event) => {
        
        const {value, name} = event.target;
        this.setState((state) => ({
            values : {
                ...state.values,
                [name]: value,

            }
        }))
    }

    handleSubmit = (event) => {
        // Ngăn chặn hành vi reload form
        event.preventDefault();
    
        if (this.props.stud.id) {
          // Cập nhật
          this.props.onUpdateUser(this.props.user.id, this.state.values);
        } else {
          // Tạo mới
          const id = Math.floor(Math.random() * 100);
          const user = { ...this.state.values, id };
          // Làm sao để thêm object user vào array users trong store???
          // dispatch action CREATE_USER
          this.props.onCreateUser(user);
        }
      };
    
      componentDidUpdate(prevProps, prevState) {
        // Vì hàm componentDidUpdate luôn luôn được chạy sau khi props hoặc state thay đổi
        // Ta sẽ kiểm tra nếu props user thay đổi sẽ set state lại cho object values
        if (prevProps.user.id !== this.props.user.id) {
          this.setState({ values: { ...this.props.user } });
        }
      }
    
  render() {
    const { values} = this.state;
    return (
      <form onSubmit={this.handleSubmit}>
        <div className="row">
          <div className="col-sm-6">
            <div className="mb-3">
              <label htmlFor="id" className="form-label">
                Mã sinh viên
              </label>
              <input
                type="text"
                id="id"
                className="form-control"
                name="id"
                value={values.id}
                onChange={this.handleChange}
              />
            </div>

            <div className="mb-3">
              <label htmlFor="phone" className="form-label">
                Số điện thoại
              </label>
              <input
                type="text"
                id="phone"
                className="form-control"
                name="phone"
                value={values.phone}
                onChange={this.handleChange}
              />
            </div>
            </div>
            <div className="col-sm-6">
              <div className="mb-3">
                <label htmlFor="name" className="form-label">
                  Họ tên
                </label>
                <input
                  type="text"
                  id="name"
                  className="form-control"
                  name="name"
                  value={values.name}
                  onChange={this.handleChange}
                />
              </div>

              <div className="mb-3">
                <label htmlFor="email" className="form-label">
                  Email
                </label>
                <input
                  type="text"
                  id="email"
                  className="form-control"
                  name="email"
                  value={values.email}
                  onChange={this.handleChange}
                />
              </div>
            </div>

            <button className="btn btn-success">Thêm sinh viên</button>
          
        </div>
      </form>
    );
  }
}

export default StudentForm;
